mapper.select:
SELECT C.ID ID, C.TITLE TITLE, C.BODY BODY, C.CODE CODE, C.CREATED_AT CREATED_AT, C.LANG LANG, C.MEMBERID MEMBERID, IF(C.ANONYMOUS=1, '익명', M.USERNAME) AS USERNAME 
FROM CODEREVIEW C, MEMBERS M 
WHERE C.MEMBERID=M.MEMBERID AND ID=#{id}

mapper.update:
UPDATE CODEREVIEW SET
TITLE=#{title},
BODY=#{body},
CODE=#{code}
WHERE ID=#{id}
